INSERT INTO `creature_text` VALUES
(33579, 0, 0, "Pentarus, you heard the $gman:woman;. Have your mages release the shield and let these brave souls through!", 14, 0, 100, 0, 0, 0, 0, "Brann gate say 0"),
(33579, 1, 0, "Okay! Let's move out. Get into your machines; I'll speak to you from here via the radio!", 12, 0, 100, 0, 0, 0, 0, "Brann gate say 1"),
(33579, 2, 0, "Our allies are ready. Bring down the shield and make way!", 14, 0, 100, 0, 0, 0, 0, "Brann gate say 2"),
(33579, 3, 0, "The iron dwarves have been seen emerging from the bunkers at the base of the pillars straight ahead of you! Destroy the bunkers, and they'll be forced to fall back!", 12, 0, 100, 0, 0, 0, 0, "Brann gate say 3");
INSERT INTO `creature_text` VALUES
(33624, 0, 0, "Of course, Brann. We will have the shield down momentarily.", 14, 0, 100, 0, 0, 0, 0, "Archmagus Pentarus say 0"),
(33624, 1, 0, "Mages of the Kirin Tor, on Brann's Command, release the shield! Defend this platform and our allies with your lives! For Dalaran!", 14, 0, 100, 0, 0, 0, 0, "Archmagus Pentarus say 1");
